CREATE OR REPLACE FUNCTION novo_papel(descricao_arg VARCHAR(255)) RETURNS INTEGER AS
$BODY$
DECLARE
   v_id INTEGER;
BEGIN
	INSERT INTO papel(descricao)
	VALUES (descricao_arg)
	RETURNING id INTO v_id;

	RETURN v_id;
END;
$BODY$
LANGUAGE plpgsql VOLATILE;

CREATE OR REPLACE FUNCTION novo_recurso(urn_arg VARCHAR(255)) RETURNS INTEGER AS
$BODY$
DECLARE
   v_id INTEGER;
BEGIN
	INSERT INTO recurso(urn)
	VALUES (urn_arg)
	RETURNING id INTO v_id;

	RETURN v_id;
END;
$BODY$
LANGUAGE plpgsql VOLATILE;

CREATE OR REPLACE PROCEDURE autoriza_papel_recurso(papel_arg INTEGER, recurso_arg INTEGER, dominios_arg TEXT[], restrita_arg BOOL DEFAULT false) AS
$BODY$
DECLARE

BEGIN
	INSERT INTO autorizacao(papel_id, recurso_id, dominios, restrita)
	VALUES (papel_arg, recurso_arg, dominios_arg, restrita_arg);
END;
$BODY$
LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE novo_usuario(username_arg VARCHAR(125), password_arg VARCHAR(125), name_arg VARCHAR(125), papel_arg INTEGER) AS
$BODY$
DECLARE

BEGIN
	INSERT INTO usuario(username, "password", "name", papel_id)
	VALUES (username_arg, password_arg, name_arg, papel_arg);
END;
$BODY$
LANGUAGE plpgsql;

